var dx = circle1.x - circle2.x;
var dy = circle1.y - circle2.y;
var distance = Math.sqrt(dx * dx + dy * dy);//平面兩點之間距離公式

if (distance < circle1.radius + circle2.radius) { //原形半徑相加=兩圓碰撞時的距離
    // collision detected!
}
