        // ES5 函式宣告（Function Declaration）
        //function 函式名稱(參數) {
        function Add(A, B) {
            return A + B;
        }
        /* 或 */
        // ES5  函式運算式(表達式)（Function Expressions）
        // var 函式名稱 = function (參數) {
        var Add2 = function (A, B) {//匿名函式
            return A + B;
        }
        var Add3 = function add3(A, B) {//#1 非匿名函式
